import { Component, Input } from '@angular/core';
import { IProduct } from 'src/app/interfaces/i-product';

@Component({
  selector: 'app-products-item',
  templateUrl: './products-item.component.html',
  styleUrls: ['./products-item.component.css']
})
export class ProductsItemComponent {

  @Input() producte!: IProduct; 

}
<div class="col">
  <div class="card" style="width: 18rem">
    <img
      [src]="'https://bqhnvwfovmcxrqrsmfxr.supabase.co/storage/v1/object/public/products/'+producte.asin+'.jpg'"
      class="card-img-top"
      [alt]="producte.asin + ' IMG '"
    />
    <div class="card-body">
      <h5 class="card-title">{{ producte.asin }}</h5>
    </div>
    <ul class="list-group list-group-flush">
      <app-reviews-list [product]="producte"></app-reviews-list>
    </ul>
    <div class="card-body"></div>
  </div>
</div>
